import React, { useEffect, useState } from "react";
import { Row } from "react-bootstrap";
import CardMovie from "./CardMovie";
import PaginationPage from "./Pagination";
import { useDispatch, useSelector } from "react-redux";
import { getAllMoviesAction } from "../redux/actions/movieActions";

function MoviesList() {
  const dispatch = useDispatch();

  // Accessing the movies state from Redux store using useSelector
  const movies = useSelector((state) => state.movies);

  // Fetch movies on component mount
  useEffect(() => {
    dispatch(getAllMoviesAction());
  }, [dispatch]);
  console.log(movies);
  /* const [movies, setMovies] = useState([]);

  const dispatchGetAllMovies = useDispatch();
  const dataMovies = useSelector((state) => state.movies);
  useEffect(() => {
    dispatchGetAllMovies(getAllMoviesAction());
    setMovies(dataMovies);
    console.log(movies);
  }, [dataMovies, movies, dispatchGetAllMovies]);*/

  return (
    <Row className="mt-3">
      {movies.length >= 1 ? (
        movies.map((movie) => {
          return <CardMovie key={movie.id} movie={movie} />;
        })
      ) : (
        <h1 className="text-center p-5"> لا يوحد أفلام</h1>
      )}
      {movies.length >= 1 ? <PaginationPage /> : null}
    </Row>
  );
}

export default MoviesList;
