import "./App.css";
import NavBar from "./components/NavBar";
import { Container } from "react-bootstrap";
import MoviesList from "./components/MoviesList";
import { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter, Router, Route, Routes } from "react-router-dom";
import MovieDetails from "./components/MovieDetails";

function App() {
  const [movies, setMovies] = useState([]);
  const [total_pages, setTotal_pages] = useState(0);
  //get all movies by axios
  const getAllMovies = async () => {
    const res = await axios.get(
      "https://api.themoviedb.org/3/movie/popular?api_key=52ef927bbeb21980cd91386a29403c78&l&language=ar"
    );
    setTotal_pages(res.data.total_pages);
    setMovies(res.data.results);
  };
  //get current page
  const getCurrentPage = async (page) => {
    const res = await axios.get(
      "https://api.themoviedb.org/3/movie/popular?api_key=52ef927bbeb21980cd91386a29403c78&l&language=ar&page=" +
        page
    );

    setMovies(res.data.results);
    setTotal_pages(res.data.total_pages);
  };
  //search movies
  const search = async (word) => {
    if (word === undefined || word === "") {
      getAllMovies();
    } else {
      const res = await axios.get(
        "https://api.themoviedb.org/3/search/movie?api_key=52ef927bbeb21980cd91386a29403c78&l&query=" +
          word +
          "&language=ar"
      );

      setMovies(res.data.results);
      setTotal_pages(res.data.total_pages);
    }
  };
  useEffect(() => {
    getAllMovies();
    //console.log(movies);
  }, []);
  return (
    <div className="font color-body">
      <NavBar search={search} />
      <Container>
        <BrowserRouter>
          <Routes>
            <Route
              path="/"
              element={
                <MoviesList
                  movies={movies}
                  getCurrentPage={getCurrentPage}
                  total_pages={total_pages}
                ></MoviesList>
              }
            ></Route>
            <Route path="/movie/:id" element={<MovieDetails />}></Route>
          </Routes>
        </BrowserRouter>
      </Container>
    </div>
  );
}

export default App;
