import axios from "axios";
import React, { useEffect, useState } from "react";
import { Col, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";

const MovieDetails = () => {
  const param = useParams();
  console.log(param);
  const [movie, setMovies] = useState([]);
  //get  movie details by axios
  const getMovieDetails = async () => {
    const res = await axios.get(
      "https://api.themoviedb.org/3/movie/" +
        param.id +
        "?api_key=52ef927bbeb21980cd91386a29403c78&l&language=ar"
    );
    console.log(res.data);
    setMovies(res.data);
  };
  useEffect(() => {
    getMovieDetails();
    //console.log(movies);
  }, []);
  return (
    <div>
      <Row className="justify-centent-center">
        <Col md="12" sm="12" xs="12" className="mt-4">
          <div className="card-details d-flex align-items-center">
            <img
              className="img-movie w-30"
              src={`https://image.tmdb.org/t/p/w500` + movie.poster_path}
              alt="ascad"
            />
          </div>
          <div className=" card-details justify-centent-center text-center mx-auto">
            <p className="card-text-details border-bottom">
              اسم الفيلم :{movie.title}
            </p>
            <p className="card-text-details border-bottom">
              تاريخ الفيلم :{movie.release_date}
            </p>
            <p className="card-text-details border-bottom">
              عدد المقيمين :{movie.vote_count}
            </p>
            <p className="card-text-details border-bottom">
              التقييم :{movie.vote_average}
            </p>
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default MovieDetails;
