import axios from "axios";
import { AllMOVIES, FETCH_MOVIES_ERROR, MOVIESAPI } from "../types/moviesTypes";

/*export const getAllMoviesAction = () => {
  return (dispatch) => {
    axios.get(MOVIESAPI).then((res) => {
      console.log(res.data.results);
      dispatch({
        type: AllMOVIES,
        data: res.data.results,
        pages: res.data.total_pages,
      });
    });
  };
};
*/
export const getAllMoviesAction = () => async (dispatch) => {
  try {
    const response = await axios.get(MOVIESAPI);
    console.log(response.data.results);

    dispatch({
      type: AllMOVIES,
      payload: {
        data: response.data.results,
        pages: response.data.total_pages,
      },
    });
  } catch (error) {
    console.error("Error fetching movies:", error);
    dispatch({
      type: FETCH_MOVIES_ERROR,
      payload: error.message,
    });
  }
};
export const getMovieSearch = (word) => {
  return (dispatch) => {
    const res = axios.get(
      "https://api.themoviedb.org/3/search/movie?api_key=52ef927bbeb21980cd91386a29403c78&l&query=" +
        word +
        "&language=ar"
    );
    console.log(res.data.results);
    dispatch({
      type: AllMOVIES,
      data: res.data.results,
      pages: res.data.total_pages,
    });
  };
};
export const getPage = (page) => {
  return (dispatch) => {
    const res = axios.get(
      "https://api.themoviedb.org/3/movie/popular?api_key=52ef927bbeb21980cd91386a29403c78&language=ar&page=" +
        page
    );
    console.log(res.data.results);
    dispatch({
      type: AllMOVIES,
      data: res.data.results,
      pages: res.data.total_pages,
    });
  };
};
